class SpatialSoundMatrix {
	std::vector<std::vector<float>> data;

public:
	SpatialSoundMatrix(std::vector<std::vector<float>> data)
	{
		this->data = data;
	}

	std::vector<std::vector<float>>& getData()
	{
		return data;
	}
};

